import React, { useState, useEffect } from 'react';

function ArtifactCard() {
  const [artifacts, setArtifacts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5015/api/artifacts')
      .then((response) => response.json())
      .then((data) => setArtifacts(data))
      .catch((error) => console.error('Error fetching artifacts:', error));
  }, []);

  return (
    <div className="artifact-list">
      {artifacts.map((artifact) => (
        <div key={artifact.id} className="artifact-card">
          <img src={artifact.image} alt={artifact.name} className="artifact-image" />
          <div className="artifact-details">
            <h2>{artifact.name}</h2>
            <p>{artifact.artist}</p>
            <p>{artifact.description}</p>
            <img src={artifact.image} alt={artifact.name} className="artifact-detail-image" />
          </div>
        </div>
      ))}
    </div>
  );
}

export default ArtifactCard;
