import React, { useState, useEffect } from 'react';
import './'; // Import the CSS

function ArtifactsList() {
  const [artifacts, setArtifacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5015/api/artifacts')
      .then(response => {
        if (!response.ok) {
            console.log("reached");
          throw new Error(`Server responded with status: ${response.status} - ${response.statusText}`);
        }
        return response.json();
      })
      .then(data => {
        setArtifacts(data);
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="cards">
      <h1>Artifacts</h1>
      <div className="cards__container">
        {artifacts.map(artifact => (
          <div key={artifact.id} className="cards__item">
            <div className="cards__item__link">
              <img src={artifact.image} alt={artifact.name} className="cards__item__img fade-img" data-category={artifact.name} />
              <div className="cards__item__info">
                <h2>{artifact.name}</h2>
                <p>Artist: {artifact.artist}</p>
                <p>Description: {artifact.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ArtifactsList;
